package learn.ioc.annotation.config;import learn.ioc.annotation.Speck;import learn.ioc.annotation.SpeckImpl;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.Configuration;@Configuration@ComponentScan("learn.ioc.annotation")public class DemoConfig {    @Bean    public Speck getSpeak(){        Speck s = new SpeckImpl();        return s;    }}